import numpy as npimport matplotlib.pyplot as pltimport pandas as pdfrom netCDF4 import Datasetfrom matplotlib.colors import LightSourcedef get_xyz(fname = 'italy-90m.grd'):    top = Dataset(fname)    x_range = top.variables['x_range'][:]    y_range = top.variables['y_range'][:]    z_range = top.variables['z_range'][:]    spacing = top.variables['spacing'][:]    dimension=top.variables['dimension'][:]    x = np.arange(x_range[0], x_range[1]+spacing[0], spacing[0])    y = np.arange(y_range[0], y_range[1]+spacing[1], spacing[1])    z = top.variables['z'][:]    z = z.reshape(y.size, x.size).T        y = y[::-1]    # plt.imshow(z); exit()    xboo = (x < 15) * (x>12)    yboo = (y < 44) * (y > 42)     # xboo[:] = True; yboo[:] = True    xl = x[xboo]    yl = y[yboo]    zl = z[xboo,:][:, yboo]    d=300    xl = xl[::d]    yl = yl[::d]    zl = zl[::d][:,::d]    return xl, yl, zlx, y, z = get_xyz()# ls = LightSource(azdeg=315, altdeg=45)# cmap = plt.cm.gist_earth# thing = ls.hillshade(z.copy(), #                vert_exag=1, dx=x[2]-x[1], dy=y[2]-y[1])plt.figure()plt.contourf(x, y, z.T, 80, cmap=plt.get_cmap('terrain'), alpha = 0.1)# plt.clim(vmin = -100, vmax = 500)plt.show()