from sklearn.cluster import DBSCANfrom sklearn.cluster import OPTICSimport numpy as npimport matplotlib.pyplot as pltfrom mpl_toolkits.mplot3d import Axes3D  # noqa: F401 unused importX = np.array([[1, 2], [2, 2], [2, 3],[8, 7], [8, 8], [25, 80]])length = 500lines = 5X = np.array([np.linspace(-1, 1, length), np.zeros(length), np.zeros(length)]).Tfor i in range(3):    X[:,i] +=  np.random.normal(0, .0005, length)for i in range(lines):    X2 = X.copy()    X2[:,1] += i*2    X = np.append(X, X2, axis = 0)    # fig= plt.figure()# ax = fig.add_subplot(111, projection='3d')# ax.scatter(X[:,0], X[:,1], X[:,2])# plt.scatter(X[:,0], X[:,1])# # clusteringd = DBSCAN(eps=.1, min_samples=200, algorithm = 'ball_tree').fit(X)clusteringo = OPTICS(min_samples=200).fit(X)fig= plt.figure()# ax = fig.add_subplot(121, projection='3d')# ax.scatter(X[:,0], X[:,1], X[:,2], c = clusteringd.labels_)ax = fig.add_subplot(122, projection='3d')ax.scatter(X[:,0], X[:,1], X[:,2], c = clusteringo.labels_)